import React from 'react';
import Form from 'react-bootstrap/Form';
import Button from 'react-bootstrap/Button';
import axios from 'axios';
import cs from './Draw_graphs.module.css';
import fconfig from "../fconfig.json";
import Col from 'react-bootstrap/Col';
import Row from 'react-bootstrap/Row';

var base_url = JSON.stringify(fconfig["api"]["root"]).replace(/['"]+/g, '')


class DrawGraphs extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      isToggleOn: true,
      isFetching: true,
      error: null,
      data : null,
      start_date : "2020-01-10",
      end_date : "2020-01-14",
      city_name : "Paris"
    }

    //    this.handleClick = this.handleClick.bind(this);
    this.MyUpdateState = this.MyUpdateState.bind(this);
  }

  ///////////////////////
  MyUpdateState(event) {

    axios.get(base_url+'/RR/api/v1.0/data/get',{
      params: {
        city : this.state.city_name,
        start_date : this.state.start_date,
        end_date : this.state.end_date
      },
      headers: {
        // Перечисляем разрешённые методы
        "Access-Control-Allow-Methods": "GET"
      }
    })
    .then(
      (response) => {

        if ('error' in response.data)
        throw new Error(response.data['error']);

        console.log(response) // выводим в консоль получаемые обьекты
        this.setState({
          data: response.data
        });
      }
    )
    .catch(e => console.log(e));

  }



  //////////////////////
  componentDidMount(event) {

    this.MyUpdateState(event)

  }
  /////////////////////


  render() {
    return (
      <div>
        <div className={cs.page_wrapper}>
          <div className={cs.form_data_wrapper}>
            <Form>
              <Form.Row>

                <Form.Group  as={Col} controlId="formHorizontal1">
                  <div className={cs.row_three_item}>
                    <Form.Label bsPrefix={cs.title_field}><b>Начальная дата:</b></Form.Label>
                    <Form.Control name="name" type="text" required="required"  onChange={this.onChange} />
                  </div>
                </Form.Group>

                <Form.Group as={Col} controlId="formHorizontal2">
                  <div className={cs.row_three_item}>
                    <Form.Label bsPrefix={cs.title_field}><b>Конечная дата:</b></Form.Label>
                    <Form.Control name="surname" type="text" required="required" onChange={this.onChange} />
                  </div>
                </Form.Group>

                <Form.Group as={Col} controlId="formHorizontal2">
                  <div className={cs.row_three_item}>
                    <Form.Label bsPrefix={cs.title_field}><b>Название города:</b></Form.Label>
                    <Form.Control name="surname" type="text" required="required" onChange={this.onChange} />
                  </div>
                </Form.Group>
              </Form.Row>

              <Row>
                <Col>
                  <div className={cs.aligin_bottom} >
                    <Button type="submit" className={cs.button_upload}  onClick={this.handleClick}>
                      Загрузить данные
                    </Button>
                  </div>
                </Col>
                <Col>
                  <div className={cs.aligin_bottom} >
                    <Button type="submit" className={cs.button_upload}  onClick={this.handleClick}>
                      Обновить данные
                    </Button>
                  </div>
                </Col>
              </Row>


            </Form>
          </div>
        </div>
      </div>
    )
  }

}


export default DrawGraphs;
